class BackgroundAudio extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){const t=this.getAttribute("audio-src"),o=this.getAttribute("attribution-text");this.shadowRoot.innerHTML=`\n        <style>\n          @import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css');\n\n          .audio-control {\n            position: absolute;\n            bottom: 40px;\n            left: 50%;\n            transform: translateX(-30%);\n            cursor: pointer;\n            font-size: 42px;\n            z-index: 9999;\n            color: #000;\n          }\n          .footer--attribute {\n            text-align: center;\n            padding: 5px 10px;\n            position: fixed;\n            width: 100%;\n            bottom: 0;\n            color: #000;\n            left: 0;\n            z-index: 999;\n            font-size: 0.75rem;\n            background-color: transparent;\n          }\n        </style>\n        <audio id="background-audio" type="audio/mp3" src="${t}" loop></audio>\n        <i id="audio-control" class="fas fa-volume-up audio-control"></i>\n        <div class="footer--attribute">\n          <p>${o}</p>\n        </div>\n      `,this.initAudioControl()}initAudioControl(){const t=this.shadowRoot.getElementById("background-audio"),o=this.shadowRoot.getElementById("audio-control");let n=!0;o.addEventListener("click",(()=>{n?(t.play(),o.classList.add("fa-volume-mute"),o.classList.remove("fa-volume-up")):(t.pause(),o.classList.add("fa-volume-up"),o.classList.remove("fa-volume-mute")),n=!n}))}}customElements.define("background-audio",BackgroundAudio);