class FullscreenButton extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){this.shadowRoot.innerHTML='\n        <style>\n          @import url(\'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css\');\n\n          .fullscreen-control {\n            position: fixed;\n            top: 10px;\n            right: 10px;\n            cursor: pointer;\n            font-size: 42px;\n            z-index: 9999;\n            color: #333;\n          }\n            @media (max-width: 768px) {\n                :host {\n                    display: none\n                }\n            }\n        </style>\n        <i id="fullscreen-control" class="fas fa-expand fullscreen-control"></i>\n      ',this.initFullscreenControl()}initFullscreenControl(){const e=this.shadowRoot.getElementById("fullscreen-control");let n=!1;e.addEventListener("click",(()=>{n?(document.exitFullscreen?document.exitFullscreen():document.mozCancelFullScreen?document.mozCancelFullScreen():document.webkitExitFullscreen?document.webkitExitFullscreen():document.msExitFullscreen&&document.msExitFullscreen(),e.classList.add("fa-expand"),e.classList.remove("fa-compress")):(document.documentElement.requestFullscreen?document.documentElement.requestFullscreen():document.documentElement.mozRequestFullScreen?document.documentElement.mozRequestFullScreen():document.documentElement.webkitRequestFullscreen?document.documentElement.webkitRequestFullscreen():document.documentElement.msRequestFullscreen&&document.documentElement.msRequestFullscreen(),e.classList.add("fa-compress"),e.classList.remove("fa-expand")),n=!n}))}}customElements.define("fullscreen-button",FullscreenButton);