const organicAnimateSpirograph=(r,e,i,n,o,a,p,t,g)=>{const s=(r,e,i,n,o)=>(r-e)*(o-n)/(i-e)+n,c=g.noise1(r),m=g.noise1(r+1e4),A=s(c,0,1,i,n),d=s(m,0,1,o,a),h=s(c,0,1,p,t);e.R=A,e.r=d,e.p=h,e.reps=10,e.render(),window.requestAnimationFrame(()=>organicAnimateSpirograph(r+1e-7,e,i,n,o,a,p,t,g))};export default organicAnimateSpirograph;